import Expenses from "./components/Expenses/Expenses";
import NewExpense from "./components/NewExpense/NewExpense";

 //Array
 const DUMMY_DATA = [
  {
    id: 'e1',
    title: 'Toilet Paper',
    amount: 94.12,
    date: new Date(2020, 7, 14),
  },
  { id: 'e2', title: 'New TV', amount: 799.49, date: new Date(2021, 2, 12) },
  {
    id: 'e3',
    title: 'Car Insurance',
    amount: 294.67,
    date: new Date(2021, 2, 28),
  },
  {
    id: 'e4',
    title: 'New Desk (Wooden)',
    amount: 450,
    date: new Date(2021, 5, 12),
  },
];

function App() {

  const [expenses,setEnteredExpenses] = useState(DUMMY_DATA);

  //Create a function to pass down data from a child component to a parent
  const addExpenseHandler = (expense) => {
    console.log('In App.js');
    console.log(expense);

  }


  return (
    <div>
       {/* Follow the same parent to communicate to a parent */}
       {/* Create a pointer that points to the addExpenseHandler function */}
      <NewExpense onAddExpense={addExpenseHandler}/>
      <Expenses expensesList = {expenses}/>
    </div>
  );
}

export default App;
