import React, {useState} from 'react'
import ExpenseItem from './ExpenseItem';
import ExpensesFilter from './ExpensesFilter'
import Card from '../UI/Card'

import './Expenses.css';

const Expenses = (props) => {


    //Create the state 
    const [enteredYear, setEnteredYear] = useState('2020');

    //Create a function to pass down data from a child component to a parent
    const ExpenseFilterHandler = (selectedYear) => {

        console.log('In Expenses.js');
        console.log(selectedYear);

        //Assign the value to the actual state
        setEnteredYear(selectedYear);


    }

    return (
        <div>
            <Card className="expenses">
            {/* Create a pointer that points to the ExpenseFilterHandler function */}
            {/* One thing to take into consideration is that the default year never changes once you filter a year, what you can do is pass another props and use two way binding  */}
            <ExpensesFilter defaultYear={enteredYear} onAddChangeFilter = {ExpenseFilterHandler}/>

                {props.expensesList.map((x) => (<ExpenseItem title={x.title} amount={x.amount} date={x.date}/>))}

                {/* <ExpenseItem title = {props.expensesList[0].title} amount={props.expensesList[0].amount} date = {props.expensesList[0].date}></ ExpenseItem >
                <ExpenseItem title = {props.expensesList[1].title} amount={props.expensesList[1].amount} date = {props.expensesList[1].date}></ ExpenseItem >
                <ExpenseItem title = {props.expensesList[2].title} amount={props.expensesList[2].amount} date = {props.expensesList[2].date}></ ExpenseItem >
                <ExpenseItem title = {props.expensesList[3].title} amount={props.expensesList[3].amount} date = {props.expensesList[3].date}></ ExpenseItem > */}
            </Card>
        </div>
    )
}

export default Expenses;
